/**
 * Type declarations for ol-ext library
 */

declare module 'ol-ext' {
  // Export a general namespace to allow any property access
  const olExt: any;
  export default olExt;
}

declare module 'ol-ext/layer/*' {
  const layerModule: any;
  export default layerModule;
}

declare module 'ol-ext/control/*' {
  const controlModule: any;
  export default controlModule;
}

declare module 'ol-ext/interaction/*' {
  const interactionModule: any;
  export default interactionModule;
}

declare module 'ol-ext/style/*' {
  const styleModule: any;
  export default styleModule;
}

declare module 'ol-ext/filter/*' {
  const filterModule: any;
  export default filterModule;
}

declare module 'ol-ext/util/*' {
  const utilModule: any;
  export default utilModule;
}

declare module 'ol-ext/geom/*' {
  const geomModule: any;
  export default geomModule;
}

declare module 'ol-ext/featureanimation/*' {
  const featureAnimationModule: any;
  export default featureAnimationModule;
}

declare module 'ol-ext/overlay/*' {
  const overlayModule: any;
  export default overlayModule;
}

declare module 'ol-ext/render/*' {
  const renderModule: any;
  export default renderModule;
}

declare module 'ol-ext/source/*' {
  const sourceModule: any;
  export default sourceModule;
}

declare module 'ol-ext/utils/*' {
  const utilsModule: any;
  export default utilsModule;
}
